import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import ProductsHome from '../Product Page/ProductsHome';

function LoginForm({ Login, error }) {
    const [info, setInfo] = useState({name:"", password:""});

    const submitHandler = e => {
        e.preventDefault();

        Login(info);
    }

    const navigate = useNavigate();

    const gotoproducts = () => {
        navigate('/productpage');
    }

    return (
        <form className='login-form' onSubmit={submitHandler}> 
            <div className="innerform">
                <h2>LogIn Portal</h2>
                {(error != "") ? (<div className="error">{error}</div>) : ""}
                <div className="boxform">
                    <label htmlFor="name"> Username:</label>
                    <input type="text" placeholder= "Enter your Username" name="username" 
                    id="username" onChange={e => setInfo({...info, name: e.target.value})} value={info.name}/>
                </div>
                <div className="boxform">
                    <label htmlFor="password"> Password:</label>
                    <input type="password" placeholder= "Enter your Password" name="password" id="password" onChange={e => setInfo({...info, password: e.target.value})} value={info.password}/>
                </div>
                <Button type="submit" value="LOGIN" onClick={() => gotoproducts()}/>
            </div>
        </form>
    )
}

export default LoginForm;
