import React, { useState } from 'react';
import LoginForm from './components/Login Page/LoginForm';
import ProductsHome from './components/Product Page/ProductsHome';
/*import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';*/
import {Routes, Route} from 'react-router-dom';
 

function App() {
    const adminuser = {
        name: "rohit",
        password: "rohit123"
    }
  
    const [user, setUser] = useState({name: "", password: ""});
    const [error, setError] = useState("");

    const Login = info => {
        console.log(info);

        if(info.name == adminuser.name && 
            info.password == adminuser.password)
        {
            console.log("Logged in");
            setUser({
                name: info.name,
                password: info.password
            });
        } else {
            console.log("Incorrect details. Please try again !");
            setError("Incorrect details. Please try again !");
        }
    }

    const Logout = () => {
        setUser({name: "", password: ""});
    }

    return (
        <div className="App"> 
            <Router>
                <LoginForm/>
            </Router>

        </div>
  );
}

export default App;
